    public static void main(String[] args)
    {

        for (int i = 0; i < 100; i++)
        {
            for (int j = 0; j < 100; j++)
            {
                if (pattern[j + i * 100] == 1)
                    System.out.print("■");
                else
                    System.out.print("□");

            }
            System.out.println();
        }

        System.out.println("Pattern length before compression " + pattern.length + " bits");
            
        String output = "";
        int counter = 0;
        int previous = 0;
        int noOfOnes = 0;
        int noOfZeros = 0;
        int longestRepeat = 0;
        for (int i = 0; i < 1000; i++){
            if (pattern[i] == 1){
                noOfOnes++;
            } else{
                noOfZeros++;
            }
            
            if (pattern[i] != previous){
                output += counter + ", ";
                if (counter > longestRepeat){
                    longestRepeat = counter;
                }
                counter = 0;
                previous = pattern[i];
            }
            counter++;
        }
        
        output += counter;
        System.out.println(output);
        System.out.println("The number of 1s are "+noOfOnes);
        System.out.println("The number of 0s are "+noOfZeros);
        System.out.println("The longest uninterrupted string of repeats was "+longestRepeat);
    }
